package Ermakov.FW;// Блок 1public class Human {    Name name;    Human father;    int height;    public Human(Name name, int height) {        this.name = name;        this.height = height;    }    public Human(Name name, int height, Human father) {        this.name = name;        this.height = height;        this.father = father;    }    String FullName(){        String fullName;        if(father == null){            fullName = "Человек с Именем " + name + " и ростом " + height;        }else{            if(name.familyName == null){                name.familyName = father.name.familyName;            }            if(name.fatherName == null){                name.fatherName = father.name.name + "ович";            }            fullName = "Человек с Именем " + name + ". Отцом " + father.name + " и ростом " + height;        }        return fullName;    }    @Override    public String toString() {        return FullName();    }}