package university.lesson1;// Блок 1public class Human {    private Name name;    private Human father;    private int height;    public Human(Name name, int height) {        this.name = name;        this.height = height;    }    public Human(Name name, int height, Human father) {        this.name = name;        this.height = height;        this.father = father;    }    public Name getName() {        return name;    }    public void setName(Name name) {        this.name = name;    }    public int getHeight() {        return height;    }    public void setHeight(int height) {        this.height = height;    }    private String setFullName(){        String fullName;        if(father == null){            fullName = "Человек с Именем " + name + " и ростом " + height;        }else{            if(name.getSecondName() == null){                name.setSecondName(father.getName().getSecondName());            }            if(name.getFatherName() == null){                name.setFatherName(father.getName().getFirstName() + "ович");            }            fullName = "Человек с Именем " + name + ", отцом " + father.getName() + " и ростом " + height;        }        return fullName;    }    @Override    public String toString() {        return setFullName();    }}